mapper.update:
update `order`
set duration = #{duration},
    price    = #{price},
    endDate  = #{endDate},
    qrCode=#{qrCode}
where orderNumber = #{orderNumber}

mapper.select:
select *
from `order`
where customerName = #{customerName}
  and orderType = #{orderType}
  and startDate >= #{statrDate}
  and endDate < #{endDate}
ORDER BY startDate DESC

select *
from `order`
where merchantName = #{merchantName}
  and orderType = #{orderType}
  and startDate >= #{statrDate}
  and endDate < #{endDate}
ORDER BY startDate DESC

mapper.update:
update `order`
set state = '已取消'
where orderNumber = #{orderNumber}
